/* Minified JavaScript for Soul Reapers Esports */
document.addEventListener('DOMContentLoaded',function(){const hamburger=document.querySelector('.hamburger');const navMenu=document.querySelector('.nav-menu');const navLinks=document.querySelectorAll('.nav-link');hamburger.addEventListener('click',function(){hamburger.classList.toggle('active');navMenu.classList.toggle('active')});navLinks.forEach(link=>{link.addEventListener('click',function(){hamburger.classList.remove('active');navMenu.classList.remove('active')})});const observerOptions={threshold:0.1,rootMargin:'0px 0px -50px 0px'};const observer=new IntersectionObserver(function(entries){entries.forEach(entry=>{if(entry.isIntersecting){entry.target.style.opacity='1';entry.target.style.transform='translateY(0)';}})},observerOptions);const animateElements=document.querySelectorAll('.player-card,.staff-card,.owner-card,.stat,.value,.contact-item');animateElements.forEach(el=>{el.style.opacity='0';el.style.transform='translateY(20px)';el.style.transition='opacity 0.6s ease,transform 0.6s ease';observer.observe(el)});const currentPath=window.location.pathname;const currentPage=currentPath.split('/').pop()||'index.html';navLinks.forEach(link=>{const linkPath=link.getAttribute('href');if(linkPath===currentPage){link.classList.add('active')}});const socialCards=document.querySelectorAll('.social-card');socialCards.forEach(card=>{card.addEventListener('mouseenter',function(){this.style.transform='translateY(-10px) scale(1.05)'});card.addEventListener('mouseleave',function(){this.style.transform='translateY(-5px) scale(1)'})});const playerCards=document.querySelectorAll('.player-card');playerCards.forEach(card=>{card.addEventListener('mouseenter',function(){const socials=this.querySelector('.player-socials');if(socials){socials.style.opacity='1';socials.style.transform='translateY(0)'}});card.addEventListener('mouseleave',function(){const socials=this.querySelector('.player-socials');if(socials){socials.style.opacity='0.8';socials.style.transform='translateY(10px)'}})});const staffCards=document.querySelectorAll('.staff-card');staffCards.forEach(card=>{card.addEventListener('mouseenter',function(){const socials=this.querySelector('.staff-socials');if(socials){socials.style.opacity='1';socials.style.transform='translateY(0)'}});card.addEventListener('mouseleave',function(){const socials=this.querySelector('.staff-socials');if(socials){socials.style.opacity='0.8';socials.style.transform='translateY(10px)'}})});const ownerCards=document.querySelectorAll('.owner-card');ownerCards.forEach(card=>{card.addEventListener('mouseenter',function(){const socials=this.querySelector('.owner-socials');if(socials){socials.style.opacity='1';socials.style.transform='translateY(0)'}});card.addEventListener('mouseleave',function(){const socials=this.querySelector('.owner-socials');if(socials){socials.style.opacity='0.8';socials.style.transform='translateY(10px)'}})});const smoothScroll=function(target){const element=document.querySelector(target);if(element){const headerOffset=80;const elementPosition=element.getBoundingClientRect().top;const offsetPosition=elementPosition+window.pageYOffset-headerOffset;window.scrollTo({top:offsetPosition,behavior:'smooth'})}};document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener('click',function(e){e.preventDefault();const target=this.getAttribute('href');if(target!=='#'){smoothScroll(target)}})});const navbar=document.querySelector('.navbar');let lastScrollTop=0;window.addEventListener('scroll',function(){const scrollTop=window.pageYOffset||document.documentElement.scrollTop;if(scrollTop>lastScrollTop&&scrollTop>100){navbar.style.transform='translateY(-100%)'}else{navbar.style.transform='translateY(0)'}lastScrollTop=scrollTop});const lazyImages=document.querySelectorAll('img[data-src]');const imageObserver=new IntersectionObserver(function(entries,observer){entries.forEach(entry=>{if(entry.isIntersecting){const img=entry.target;img.src=img.dataset.src;img.removeAttribute('data-src');img.classList.add('loaded');imageObserver.unobserve(img)}})});lazyImages.forEach(img=>{imageObserver.observe(img)});const forms=document.querySelectorAll('form');forms.forEach(form=>{form.addEventListener('submit',function(e){e.preventDefault();const submitBtn=form.querySelector('button[type="submit"]');if(submitBtn){submitBtn.disabled=true;submitBtn.textContent='Sending...';setTimeout(function(){submitBtn.disabled=false;submitBtn.textContent='Send Message';form.reset()},2000)}})});const videoPlaceholders=document.querySelectorAll('.video-placeholder');videoPlaceholders.forEach(placeholder=>{placeholder.addEventListener('click',function(){this.innerHTML='<div style="text-align:center;padding:2rem;"><i class="fas fa-play-circle" style="font-size:3rem;color:#ff0040;margin-bottom:1rem;"></i><p>Video coming soon...</p></div>'})})});
